    private void jButton15ActionPerformed(java.awt.event.ActionEvent evt) {                                                   if (!jTextField1.getText().contains(".")) {             if (jTextField1.getText().equals("0")) {                 bil += "0.";                 jTextField1.setText(bil);             } else {                 bil += ".";                 jTextField1.setText(bil);             }         }     }                                          
 
    private void jButton10ActionPerformed(java.awt.event.ActionEvent evt) {                                                   bil2 = Double.parseDouble(jTextField1.getText());            switch (pilih) {             case 1:                 jumlah = bil1 / bil2;                 break;             case 2:                 jumlah = bil1 * bil2;                 break;             case 3:                 jumlah = bil1 - bil2;                 break;             case 4:                 jumlah = bil1 + bil2;                 break;         }         bil = "";         jTextField1.setText(String.valueOf(jumlah));     }                                          
 
